mapper.insert:
insert into tbl_activity( id, owner, name, startDate, endDate, cost, description, createTime, createBy
) values ( #{id}, #{owner}, #{name}, #{startDate}, #{endDate}, #{cost}, #{description}, #{createTime}, #{createBy}
)

mapper.select:
select a.id,u1.name as owner,a.name,a.startDate,a.endDate,a.cost, a.description,a.createTime,u2.name as createBy,a.editTime,u3.name as editBy from tbl_activity a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.createBy=u2.id left join tbl_user u3 on a.edit=u3.id
and a.name like '%' #{name} '%'
and u1.name like '%' #{owner} '%'
and a.startDate >= #{startDate}
and a.endDate <= #{endDate}
order by a.createTime desc limit #{beginNo},#{pageSize}

select a.id,u1.name as owner,a.name,a.startDate,a.endDate,a.cost, a.description,a.createTime,u2.name as createBy,a.editTime,u3.name as editBy from tbl_activity a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.createBy=u2.id left join tbl_user u3 on a.edit=u3.id
and a.name like '%' #{name} '%'
and u1.name like '%' #{owner} '%'
and a.startDate >= #{startDate}
and a.endDate <= #{endDate}
